import React, { useEffect, useState } from 'react'
import Navbar from './Navbar'
import featured, { wordpress } from './Carddata';
import Alltheme from "./Alltheme"
import Footer from './Footer';

const Subcat = (props) => {
    const [count,setcount] = useState(
        {
            start:"0",
            end:"4"
        }
    )
    const btn = document.querySelector(".paginations");
    return (
        <>
            <Navbar />
            <div className="alltheme">

                <div className="container">

                    <div className="row">
                        <div className="col-md-9">
                            <div className="subsearch">
                                <div className="col-12 position-relative p-0">
                                    <input type="text" className="w-100  rounded border-0" placeholder="Search Here" />
                                    <button className="position-absolute"><i className="fas fa-search"></i></button>
                                </div>
                            </div>
                            <div className="sort text-right mt-3">
                                <label htmlFor="sort">Sort By</label>
                                <select name="" id="sort">
                                    <option value="">Name/Title</option>
                                    <option value="">Most Reviews</option>
                                    <option value="">Featured</option>
                                    <option value="">Newest First</option>
                                    <option value="">Price: Low To High</option>
                                    <option value="">Price: High To Low</option>
                                </select>
                                <span className="line"></span>
                            </div>
                            {
                                wordpress.slice(count.start,count.end).map(cur => {
                                    if (cur.type == props.cat) {
                                        return (
                                            <Alltheme img={cur.img}
                                                heading={cur.heading}
                                                para={cur.para}
                                                price={cur.price}
                                                rating={cur.rating}
                                            />
                                        )
                                    }
                                })
                            }
                            <div className="page text-center">
                            <button className="paginations btn btn-warning mt-5 mb-4 py-3" onClick={()=>{
                                setcount(count.end+4)
                            }}>Load more</button>

                            </div>
                        </div>
                        <div className="col-3">
                            <div className="cat mt-3 shadow">
                                <h2>CATEGORIES</h2>
                                <div className="catlist">
                                    <ul>
                                        <li><a href="">Wordpress Themes</a></li>
                                        <li><a href="">Magento Themes</a></li>
                                        <li><a href="">Opencart Templates</a></li>
                                        <li><a href="">PHP Script & Code</a></li>
                                        <li><a href="">Wordpress Plugins</a></li>
                                        <li><a href="">eCommerce</a></li>
                                    </ul>
                                </div>
                            </div>
                        </div>
                    </div>

                </div>
            </div>
            <Footer />

        </>
    )
}

export default Subcat
